%% Sistemas de controle - Lugar das raízes - controle em malha fechada%  Referência: https://wiki.octave.org/Control_package%  V.C.Parroclear all;        % Limpa as variáveisclc;              % limpa a telaclose all;        % limpa os gráficospkg load control  %% somente para usuários de Octave%%% Função em malha abertaN = 1;a = [1 1];b = [1 2];c = [1 3];aux = conv(a,b);D = conv(aux,c);Gma =tf(N,D);        % coloca no formato de laplace% Transfer function 'Gma' from input 'u1' to output ...%                1% y1:  ----------------------%       s^3 + 6 s^2 + 11 s + 6%% Lugar das raizesrlocus(Gma)%% Sistema em malha fechadaK = 60;Gmf = feedback(60*Gma,1)figure(2)impulse(Gmf)